<script setup>
import {useDigidiceStore} from "@/stores/digiDice";
import {onMounted, ref} from "vue";
const digidice = useDigidiceStore();

const pageload = ref(true);
let changer = null;

function startlLoading(){
  pageload.value = true;
  const dices = document.querySelectorAll('.containsDice svg');

  let i = 0;
  changer = setInterval(()=>{
    dices.forEach(el=>{
      if(el.classList.contains('active')) el.classList.remove('active');
    })

    if(dices[i] === undefined){
      i = 0;
    }

    dices[i].classList.add('active');
    i++;
  },500)

}

function stopLoading(){
  pageload.value = false;
  if(changer === null ) clearInterval(changer);
}

onMounted(()=>{
  if(digidice.gameStatus === 'waiting') {
    startlLoading();
    setTimeout(()=>{
      stopLoading();
    },3000)
  } else {
    stopLoading();
  }
})

</script>

